pragma solidity ^0.4.23;

contract ToDo {
    struct Task {
        uint id;
        string content;
        string author;
        uint deadLine;
        bool done;
    }

    mapping (uint => Task) taskList;

    uint[] TaskIds;
    uint lastTaskId = 0;

    function getTaskIds() public view returns (uint[]) {
        return TaskIds;
    }

    event TaskCreated(uint lastTaskId, string content, string author, uint deadLine, bool done);

    function addTask(string _content, string _author, uint _deadLine) public {
        lastTaskId ++;
        // push new Task to taskList and default the status is "false"
        taskList[lastTaskId] = Task(lastTaskId, _content, _author, _deadLine, false);
        TaskIds.push(lastTaskId);
        emit TaskCreated(lastTaskId, _content, _author, _deadLine, false);
    } 


    modifier taskExists(uint id) {
        if(taskList[id].id == 0) {
         revert(); //this will abort execution
        }
        _;
    }

    function viewTast(uint _id)
    taskExists(_id)
    public view 
    returns (uint, string, string, uint, bool) {
        return(_id, taskList[_id].content, taskList[_id].author, taskList[_id].deadLine, taskList[_id].done);
    }

    function finish(uint _id) public {
        taskList[_id].done = true;
        delete taskList[_id];
        delete TaskIds[_id-1];
    }
}
